create database mydb;
use mydb

drop table player;

create table team (
	team_id varchar(2) primary key, 
	team_name varchar(14) not null
);

create table player (
	team_id varchar(3), 
	number varchar(5),
	player_name varchar(30) not null,
	position varchar(30),
	throwing_batting varchar(4),
	height int,
	weight int,
	birthday date,
	career varchar(150),
	pro_year int,
	PRIMARY KEY(team_id, number),
	FOREIGN KEY(team_id)
	REFERENCES team(team_id)
);

alter table player modify career varchar(150)
alter table player modify position varchar(30)
alter table player modify number varchar(5)

select * from player inner join team where player.team_id = team.team_id;
